    private static void send(String url, String json1) throws IOException {
        Map<String, String> param = new HashMap();
        param.put("appId", "yusong");//该参数需要向第三方申请
        param.put("signType", "MD5");
        param.put("timestamp", "1568253068357");
        param.put("appSecret", "c264a7443730eb43ee026cd260621a07");//该参数需要向第三方申请
        String json = json1;
        param.put("JSON", json);

        String sign = AppUtils.sign(param);
        System.out.println("sign：" + sign);
        param.put("sign", sign);
        param.remove("JSON");

        Response response = postJson(url, param, json);
        System.out.println("返回：" + response.body().string());
    }

    public static Response postJson(String url, Map<String, String> params, String json) throws IOException {
        StringBuilder builder = new StringBuilder();
        for (Map.Entry<String, String> entry : params.entrySet()) {
            if (builder.length() != 0) {
                builder.append("&");
            }

            builder.append(entry.getKey());
            builder.append("=");
            builder.append(URLEncoder.encode(entry.getValue(), "UTF-8"));
        }

        if (url.indexOf("?") >= 0) {
            url += "&" + builder.toString();
        } else {
            url += "?" + builder.toString();
        }

        OkHttpClient okHttpClient = new OkHttpClient();
        MediaType JSON = MediaType.parse("application/json; charset=utf-8");
        RequestBody requestBody = FormBody.create(JSON, json);
        Request request = new Request.Builder()
                .url(url)
                .post(requestBody)
                .build();
        Response response = okHttpClient.newCall(request).execute();

        return response;
    }

